const params = $input.item.json.tool_call.parameters;
const action = params.action;

// Map n8n action names to ArchiveOps.ps1 actions
const actionMap = {
  'extract_archive': 'extract',
  'create_archive': 'create',
  'list_archive': 'list'
};

const mappedAction = actionMap[action] || action;

return {
  json: {
    action: mappedAction,
    params: params,
    original_request: $input.item.json
  }
};

const stdout = $input.item.json.stdout || '';
const stderr = $input.item.json.stderr || '';

let validationResult;
try {
  validationResult = JSON.parse(stdout);
} catch (e) {
  validationResult = {
    success: false,
    error: 'Failed to parse safety validation output',
    raw_output: stdout
  };
}

if (!validationResult.success) {
  return {
    json: {
      status: 'validation_failed',
      error: validationResult.error || 'Safety validation failed',
      validation: validationResult
    }
  };
}

// Continue to archive operation
return {
  json: {
    ...$input.item.json,
    validation: validationResult
  }
};

const stdout = $input.item.json.stdout || '';
const stderr = $input.item.json.stderr || '';

let scriptResult;
try {
  scriptResult = JSON.parse(stdout);
} catch (e) {
  scriptResult = {
    success: false,
    error: 'Failed to parse ArchiveOps output',
    raw_output: stdout
  };
}

return {
  json: {
    status: scriptResult.success ? 'success' : 'failure',
    timestamp: scriptResult.timestamp || new Date().toISOString(),
    operation: 'ArchiveOps',
    action: scriptResult.action || $input.item.json.action,
    data: scriptResult.data || null,
    error: scriptResult.error || null,
    validation: $input.item.json.validation
  }
};
