const params = $input.item.json.tool_call.parameters;
const task = params.task;
const context = params.context || '';

// Build prompt for Ollama to break down the task
const prompt = `You are a task planning assistant. Break down the following task into 3-10 sequential steps.

Task: ${task}
Context: ${context}

Provide a JSON response with this structure:
{
  "steps": [
    {"step_number": 1, "action": "...", "tool": "...", "requires_confirmation": false},
    {"step_number": 2, "action": "...", "tool": "...", "requires_confirmation": false}
  ],
  "estimated_duration": "5 minutes"
}

Available tools: file_manager, email_manager, memory_manager, vision_tool, system_info, api_tool, search_tool`;

return {
  json: {
    ollama_prompt: prompt,
    original_task: task
  }
};

const response = JSON.parse($input.item.json.response);
const plan = JSON.parse(response.response);

return {
  json: {
    success: true,
    action: 'create_plan',
    plan: plan,
    original_task: $input.item.json.original_task,
    timestamp: new Date().toISOString()
  }
};
